import { Component, EventEmitter, Input, Output, OnInit } from '@angular/core';
import { Bike } from '../../../bikes/bike.model';
import { BikeService } from '../../../bikes/bike.service';
import { BikesFilterPipe } from '../../bikes-filter.pipe'
@Component({
  selector: 'app-bike-item',
  templateUrl: './bike-item.component.html',
  styleUrls: ['./bike-item.component.css']
})

export class BikeItemComponent implements OnInit{
 @Input() bike: Bike;
 bikeSender: string;
 constructor (
  private bikeService: BikeService,

  ) {}
 ngOnInit(){
 }
 onSelected() {
   this.bikeService.bikeSelectedEvent.emit(this.bike);
 }
}
<a
  class="list-group-item clearfix documentDiv justify-content-between"
  routerLink="./{{ bike.id }}"
  routerLinkActive="active"
>
 <div>
    <span class="pull-left">
      <img
        [src]="bike.url"
        alt="{{ bike.manufacturer }}"
        class="img-responsive"
        style="max-height: 50px"
      />
    </span>
  </div>
  <div class="pull-left">
    <p class="list-group-item-heading">{{ bike.manufacturer }}</p>
  </div>
  <br>
  <div class="pull-right">
    <p>{{ bike.model }}</p>
  </div>

</a>
