import { Injectable, EventEmitter } from '@angular/core';
import { Subject } from 'rxjs';
import { Document } from './documents.model';
import { MOCKDOCUMENTS } from './MOCKDOCUMENTS';

@Injectable({
  providedIn: 'root'
})
export class DocumentService {
  document: Document[] = [];
  documentListClone: Document[] = [];
  maxid = 0;
  maxDocumentId: number;

  // Observables
  DocumentListChanged = new Subject<Document[]>();

// Event Emitters
  documentSelectedEvent = new EventEmitter<Document>();
  documentChangedEvent = new EventEmitter<Document>();

  constructor() {
    this.document = MOCKDOCUMENTS;
    this.maxDocumentId = this.getMaxDocumentId();
  };

  getDocuments() {
    return this.document.slice();
  };

  deleteDocument(document: Document) {
    if (!document) {
       return;
    }
    const pos = this.document.indexOf(document);
    if (pos < 0) {
       return;
    }
    this.document.splice(pos, 1);
    this.documentListClone = this.document.slice();
    this.DocumentListChanged.next(this.documentListClone);
 };


  getMaxDocumentId() {
    let maxId = 0;
    let currentId = 0;
      for (let document of this.document) {
        currentId = parseInt(document.id);
        if (currentId > maxId) {
         maxId = currentId;
         }
       }
       console.log(maxId);
       return maxId;
     }

addDocument(newdocument: Document) {
    if (!newdocument) {
       return;
    }
    this.maxDocumentId++;
    newdocument.id = this.maxDocumentId.toString();
    this.document.push(newdocument);
    this.documentListClone = this.document.slice();
    this.DocumentListChanged.next(this.documentListClone);
}

updateDocument(document: Document, newDocument: Document) {
     if (!document || !newDocument) {
       return;
     }
  const pos = this.document.indexOf(document);
    if (pos < 0) {
       return;
    }
  // this.document.splice(pos, 1);
  newDocument.id = document.id
  document[pos] = newDocument
  this.documentListClone = this.document.slice();
  this.DocumentListChanged.next(this.documentListClone);
}


getDocument(id: string): Document {
    for (let document of this.document) {
      if (document.id === id) {
      console.log(document);
         return document;
       }
     }
   }
}
