import {
  Component,
  OnInit,
  ElementRef,
  ViewChild,
  EventEmitter,
  Output
} from '@angular/core';

import { Message } from '../../message.model';

@Component({
  selector: 'app-message-edit',
  templateUrl: './message-edit.component.html',
  styleUrls: ['./message-edit.component.css']
})
export class MessageEditComponent implements OnInit {
  currentSender = "Layne";
  @ViewChild('subject', { static: false }) subjectInputRef: ElementRef;
  @ViewChild('msgText', { static: false }) msgTextInputRef: ElementRef;
  @Output() msgAdded = new EventEmitter<Message>();

  constructor() { }

  ngOnInit() {
  }

  onAddMessage() {
    "do nothing";
  //  const newMessage = new Message(99, "Test Subject", "Test text", this.currentSender);
  //   this.msgAdded.emit(newMessage);
  }
  // onSendMessage() {
  //   const subject = this.subjectInputRef.nativeElement.value;
  //   const msgText = this.msgTextInputRef.nativeElement.value;
  //   const newMessage = new Message(99, subject, msgText, this.currentSender);
  //   this.msgAdded.emit(newMessage);
  // }
  onClear() {
    this.subjectInputRef.nativeElement.value  = "";
    this.msgTextInputRef.nativeElement.value  = "";
    // new Message(99, subject, msgText, "joe");
    // this.msgCleared.emit(newMessage);
  }
}
<div class="panel panel-default">
  <div class="panel-body">
    <form id="document-edit">
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="subject">Subject</label>
          <input
            type="text"
            id="subject"
            class="form-control"
            size="120"
            max="120"
            #subject
          />
        </div>
        <div class="col-sm-12 form-group">
          <label for="message">Message</label>
          <input
            type="text"
            id="msgText"
            class="form-control"
            max="255"
            size="120"
            #msgText
          />
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <button class="btn btn-success" type="submit" (click)="onAddMessage()">Add</button>
          <button class="btn btn-danger" type="button">Delete</button>
          <button class="btn btn-primary" type="button">Clear</button>
          <!-- <button
            class="btn btn-success"
            type="submit"
            (click)="(onSendMessage)"
          >
            Add Message
          </button>
          <button class="btn btn-danger" type="submit" (click)="(onClear)">
            Clear
          </button> -->
        </div>
      </div>
    </form>
  </div>
</div>
