import { HttpClient, HttpHeaders } from '@angular/common/http';
import { EventEmitter, Injectable, OnInit } from '@angular/core';
import { Subject } from 'rxjs';
import { Document } from './documents.model';
import { MOCKDOCUMENTS } from './MOCKDOCUMENTS';
import { json } from 'body-parser';
import { response } from 'express';

@Injectable({
  providedIn: 'root',
})
export class DocumentService {
  // mongoURIdocuments: string = "https://wdd430cms0-default-rtdb.firebaseio.com/documents.json";
  mongoURIdocuments: string = 'http://localhost:3000/documents';
  jsondocs: Document[] = [];
  documents: Document[] = [];
  documentSelectedEvent = new EventEmitter<Document>();
  // documentChangedEvent = new EventEmitter<Document[]>();
  documentListChanged = new Subject<Document[]>();
  maxDocumentId: number;

  constructor(private httpClient: HttpClient) {
   //this.documents = MOCKDOCUMENTS;
   // this.webdocuments = this.getDocuments();
  //  this.jsondocs = this.getDocuments();
  this.documents = this.getDocuments();


   // console.log('mockDocs = ', this.documents);
   // console.log("Web Docs Array = ", this.webdocuments);
    this.maxDocumentId = this.getMaxDocumentId();
    console.log("Max Doc ID =", this.maxDocumentId);
  }

  // getDocuments() {
  //   this.httpClient
  //     .get<{ message: string; documents: Document[] }>(this.mongoURIdocuments)
  //     .subscribe((documentsData) => {
  //       console.log('It works!');
  //       this.documents = documentsData.documents;
  //       response.status(200).json({
  //         message: 'Docs fetched successfully'
  //          documents: documents });
  //       });

        //sortAndSend() {
        // this.documents.sort((a, b) => {
        //   if (a.name < b.name) {
        //     return -1;
        //   }
        //   if (a.name > b.name) {
        //     return 1;
        //   }
        //   return 0;
        // });
  //       this.documentListChanged.next(this.documents.slice());
  //     });
  // }

  getDocuments() {
    this.httpClient
      .get<{documents: Document[]}>(this.mongoURIdocuments)
      .subscribe((docs) => {
      //  this.documents = docs;
        console.log('mongoDocs =', docs);
        this.maxDocumentId = this.getMaxDocumentId();
        // Alphabetical Sort
        this.documents.sort((a, b) => {
          if (a.name < b.name) {
            return -1;
          }
          if (a.name > b.name) {
            return 1;
          }
          return 0;
        });
        this.documentListChanged.next(this.documents.slice());
      });
     return this.documents.slice();
  }

  getDocument(id: string): Document {
    for (const document of this.documents) {
      if (document.id == id) {
        //console.log("found!")
        return document;
      }
    }
    return null;
  }
  addDocument(document: Document) {
    if (!document) {
      return;
    }

    // make sure id of the new Document is empty
    document.id = '';

    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    // add to database
    this.httpClient
      .post<{ message: string; document: Document }>(
        'http://localhost:3000/documents',
        document,
        { headers: headers }
      )
      .subscribe((responseData) => {
        // add new document to documents
        this.documents.push(responseData.document);
        this.sortAndSend();
      });
  }

  // addDocument(newDocument: Document) {
  //   if (newDocument == undefined || newDocument == null) {
  //     return;
  //   }

  //   this.maxDocumentId++;
  //   newDocument.id = String(this.maxDocumentId);
  //   this.documents.push(newDocument)
  //   this.storeDocuments();
  // }

  // updateDocument(originalDocument: Document, newDocument: Document) {
  //   if (originalDocument == null || originalDocument == null || originalDocument == undefined || originalDocument == undefined) {
  //     return;
  //   }

  //   const pos = this.documents.indexOf(originalDocument);
  //   if (pos < 0) {
  //     return;
  //   }

  //   newDocument.id = originalDocument.id;
  //   this.documents[pos] = newDocument;
  //   this.storeDocuments();
  // }

  updateDocument(originalDocument: Document, newDocument: Document) {
    if (!originalDocument || !newDocument) {
      return;
    }

    const pos = this.documents.findIndex((d) => d.id === originalDocument.id);

    if (pos < 0) {
      return;
    }

    // set the id of the new Document to the id of the old Document
    newDocument.id = originalDocument.id;
    // newDocument._id = originalDocument._id;

    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    // update database
    this.httpClient
      .put(
        'http://localhost:3000/documents/' + originalDocument.id,
        newDocument,
        { headers: headers }
      )
      .subscribe((response: Response) => {
        this.documents[pos] = newDocument;
        this.sortAndSend();
      });
  }

  // deleteDocument(document: Document) {
  //   if (!document) {
  //     return;
  //   }
  //   const pos = this.documents.indexOf(document);
  //   if (pos < 0) {
  //     return;
  //   }
  //   this.documents.splice(pos, 1);
  //   this.storeDocuments();
  // }

  deleteDocument(document: Document) {
    if (!document) {
      return;
    }

    const pos = this.documents.findIndex((d) => d.id === document.id);

    if (pos < 0) {
      return;
    }

    // delete from database
    this.httpClient
      .delete('http://localhost:3000/documents/' + document.id)
      .subscribe((response: Response) => {
        this.documents.splice(pos, 1);
        this.sortAndSend();
      });
  }

  getMaxDocumentId(): number {
    //return this.documents.sort(d=>d.id)[0];
    let maxId = 0;
    for (const document of this.documents) {
      if (+document.id > maxId) {
        maxId = +document.id;
      }
    }
    return maxId;
  }

  storeDocuments() {
    this.httpClient
      .put(this.mongoURIdocuments, JSON.stringify(this.documents), {
        headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
      })
      .subscribe({
        next: () => this.documentListChanged.next(this.documents.slice()),
        error: (evar) => console.error('Error saving documents: ', evar),
      });
  }

  sortAndSend() {
    this.documents.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });
    this.documentListChanged.next(this.documents.slice());
  }
}
