import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Contact } from '../contact.model';
import { ContactService } from '../contact.service';
import { WindRefService } from '../../wind-ref.service';

@Component({
  selector: 'app-contact-detail',
  templateUrl: './contact-detail.component.html',
  styleUrls: ['./contact-detail.component.css']
})
export class ContactDetailComponent implements OnInit{
    id: string;
    strid: string;
    contact: Contact;
    nativeWindow: any;

  constructor(private windrefservice: WindRefService,
              private contactService: ContactService,
              private route: ActivatedRoute,
              private router: Router) {
              this.nativeWindow = this.windrefservice.getNativeWindow();
  }

  ngOnInit() {
    this.route.params
    .subscribe(
      (params: Params) => {
        this.id = params['id'];
        // this.id = "48";
        // console.log(this.id);
        this.contact = this.contactService.getContact(this.id);
      }
    );
  }

  onDelete() {
    this.contactService.deleteContact(this.contact);
    }


  onView() {
    // if (this.contact.url){
    //  this.nativeWindow.open(this.contact.url);
    }
  }


<div class="panel panel-default">
  <div class="panel-body">
    <div class="col-xs-12">
      <h1>{{ contact.name }}</h1>
      <span class="pull-left">
        <img
          [src]="contact.imageUrl"
          alt="{{ contact.name }}"
          class="img-responsive"
          style="max-height: 100px"
        />
      </span>
    </div>
    <br />
    <br />
    <br />
    <div class="col-sm-7"></div>
         <a class="btn btn-info pull-right" routerLinkActive="active" routerLink="../:id/edit">Edit</a>
        <a class="btn btn-danger pull-right" routerLinkActive="active" routerLink="/documents" (click)="onDelete()">Delete</a>
  </div>
</div>
<div class="panel panel-default">
  <div layout="row" class="panel-body">
    <form class="form-horizontal">
      <div class="mx-auto">
        <h4>{{ "Email: " + contact.email }}</h4>
        <h4>{{ "Phone: " + contact.phone }}</h4>
      </div>
      <div class="form-group">
      </div>
    </form>
  </div>
</div>
