import {
  Component,
  OnInit,
  ElementRef,
  ViewChild,
  EventEmitter,
  Output
} from '@angular/core';

import { Message } from '../../message.model';

@Component({
  selector: 'app-message-edit',
  templateUrl: './message-edit.component.html',
  styleUrls: ['./message-edit.component.css']
})
export class MessageEditComponent implements OnInit {
  @ViewChild('idInput', { static: false }) idInputRef: ElementRef;
  @ViewChild('subjectInput', { static: false }) subjectInputRef: ElementRef;
  @ViewChild('msgTextInput', { static: false }) msgTextInputRef: ElementRef;
  @ViewChild('senderInput', { static: false }) senderInputRef: ElementRef;
  @Output() msgAdded = new EventEmitter<Message>();

  constructor() { }

  ngOnInit() {
  }

  onAddItem() {
    const id = this.idInputRef.nativeElement.value;
    const subject = this.subjectInputRef.nativeElement.value;
    const msgText = this.msgTextInputRef.nativeElement.value;
    const sender = this.senderInputRef.nativeElement.value;
    const newMessage = new Message(id, subject, msgText, sender);
    this.msgAdded.emit(newMessage);
  }

}
<div class="panel panel-default">
  <div class="panel-body">
    <form id="document-edit">
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="subject">Subject</label>
          <input
            type="text"
            id="subject"
            class="form-control"
            size="120"
            max="120"
          />
        </div>
        <div class="col-sm-12 form-group">
          <label for="message">Message</label>
          <input
            type="text"
            id="message"
            class="form-control"
            max="255"
            size="120"
          />
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <button class="btn btn-success" type="submit">Send</button>
          <button class="btn btn-danger" type="button">Clear</button>
        </div>
      </div>
    </form>
  </div>
</div>
