//   ]
// }
<div class="panel panel-default">
  <div class="panel-body">
    <div class="col-xs-12">
      <h1>{{ document.name }}</h1>
      <span class="pull-left">
        <img
          [src]="document.description"
          alt="{{ document.name }}"
          class="img-responsive"
          style="max-height: 100px"
        />
      </span>
    </div>
    <br />
    <br />
    <br />
    <div class="col-sm-7"></div>
    <button class="btn btn-primary pull-right" type="button">Edit</button>
    <button class="btn btn-danger pull-right" type="button">Delete</button>
  </div>
</div>
<div class="panel panel-default">
  <div layout="row" class="panel-body">
    <form class="form-horizontal">
      <div class="mx-auto">
        <h4>{{ "Name: " + document.name }}</h4>
        <h4>{{ "Description: " + document.description }}</h4>
      </div>
      <div class="form-group">
      </div>
    </form>
  </div>
</div>
