import {
  Component,
  OnInit,
  ElementRef,
  ViewChild,
  EventEmitter,
  Output
} from '@angular/core';
import { Message } from '../../message.model';

@Component({
  selector: 'app-message-edit',
  templateUrl: './message-edit.component.html',
  styleUrls: ['./message-edit.component.css']
})
export class MessageEditComponent implements OnInit {
  currentSender = "Layne";
  @ViewChild('subjectInput', { static: false }) subjectInputRef: ElementRef;
  @ViewChild('msgTextInput', { static: false }) msgTextInputRef: ElementRef;
  @Output() messageAdded = new EventEmitter<Message>();

 constructor() { }

 ngOnInit() {
 }

  // addMessage() {
  // console.log("Add Message ran")
  // const newMessage = new Message(99, "Test Subject", "Test text", "Shmozer");
  // const newMessage = new Message(99, "Test Subject", "Test text", this.currentSender);
  // this.messageAdded.emit(newMessage);
  // }
  addMessage() {
  const subject = this.subjectInputRef.nativeElement.value;
  const msgText = this.msgTextInputRef.nativeElement.value;
  const newMessage = new Message(99, subject, msgText, this.currentSender);
  this.messageAdded.emit(newMessage);
  }
  onClear() {
  console.log("Clear Message ran")

    this.subjectInputRef.nativeElement.value  = "";
    this.msgTextInputRef.nativeElement.value  = "";
    // new Message(99, subject, msgText, "joe");
    // this.msgCleared.emit(newMessage);
  }
}
 <div class="panel panel-default">
  <div class="panel-body">
    <!-- <form id="document-edit"> -->
      <form>
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="subject">Subject</label>
          <input
            type="text"
            id="subjectInput"
            class="form-control"
            size="120"
            max="120"
            #subjectInput
          />
        </div>
        <div class="col-sm-12 form-group">
          <label for="message">Message</label>
          <input
            type="text"
            id="msgTextInput"
            class="form-control"
            max="255"
            size="120"
            #msgTextInput
          />
        </div>
      </div>
</form>


      <div class="row">
        <div class="col-xs-12">

          <button class="btn btn-success" type="submit"
          (click)="(addMessage())">Send</button>

          <button class="btn btn-danger" type="submit"
          (click)="(onClear())">Clear</button>

        </div>
      </div>

