import { Component, EventEmitter, Output } from '@angular/core';
import { Document } from '../documents.model';
// import { DocumentItem } from '../document';

@Component({
  selector: 'app-document-list',
  templateUrl: './document-list.component.html',
  styleUrls: ['./document-list.component.css']
})
export class DocumentListComponent {
    @Output() documentWasSelected = new EventEmitter<Document>();
    documents: Document[] = []

onDocumentSelected(document: Document) {
   this.documentWasSelected.emit(document);
  }

}
<div class="panel panel-default">
  <div class="panel-heading">
    <div class="row pad-left-right">
      <span class="pull-left title">Documents</span>
      <a class="btn btn-success pull-right">Add Document</a>
    </div>
  </div>
  <div class="panel-body">
    <div class="center-panel">
      <app-document-item
        *ngFor="let documentEl of documents"
        [document]="documentEl"
          (documentSelected)="onDocumentSelected(documentEl)">
      </app-document-item>
    </div>
  </div>
</div>
