import { Component, EventEmitter, Output } from '@angular/core';
import { Message } from '../message.model';
@Component({
  selector: 'app-message-list',
  templateUrl: './message-list.component.html',
  styleUrls: ['./message-list.component.css']
})
export class MessageListComponent {
    @Output() messageWasSelected = new EventEmitter<Message>();
    messages: Message[] = [
    new Message(1, "R. Kent Jackson", "jacksonk@byui.edu", "208-496-3771", "../../../assets/images/jacksonk.jpg", null),
    new Message(2, "Rex Barzeer", "barzeer@byui.edu", "208-496-3768", "../../../assets/images/barzeer.jpg", null),
    new Message(2, "Joe Shmozer", "barzeer@byui.edu", "208-496-3768", "../../../assets/images/barzeer.jpg", null),
    new Message(1, "Harry Howser", "jacksonk@byui.edu", "208-496-3771", "../../../assets/images/jacksonk.jpg", null),
    new Message(1, "Double Harry Howser", "jacksonk@byui.edu", "208-496-3771", "../../../assets/images/jacksonk.jpg", null)
  ];

onMessageSelected(message: Message) {
   this.messageWasSelected.emit(message);
  }

}
<div class="row">
  <div class="col-md-5">
    <div class="panel panel-default">
      <div class="panel-heading">
        <div class="row pad-left-right">
          <span class="title pull-left">Messages</span>
        </div>
      </div>
      <div class="panel-body">
        <div class="row">
          <div class="col-xs-12">
            <app-message-item></app-message-item>
            <!-- <app-message-item
        *ngFor="let messageEl of messages"
        [message]="messageEl"
          (messageSelected)="onContactSelected(messageEl)"></app-message-item> -->
          </div>
        </div>
      </div>
      <div class="panel-footer">
        <app-message-edit></app-message-edit>
      </div>
    </div>
  </div>
</div>
