import { Component, Output, EventEmitter } from "@angular/core";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html'
})
export class HeaderComponent {
  // @Output() featureSelected = new EventEmitter<string>();

  // onSelect(feature: string) {
  //   this.featureSelected.emit(feature);
  // }
}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container">
    <div class="navbar-header">
      <a href="#" class="navbar-brand">WeLearn CMS</a>
    </div>
    <div class="navbar navbar-expand">
      <ul class="nav navbar-nav">
        <li role="presentation" routerLinkActive="active"[routerLinkActiveOptions]="{ exact: true }">
          <a routerLink="documents">Documents</a>
          <!-- <a routerLink="/">Documents</a> -->
        </li>
        <li role="presentation" routerLinkActive="active"[routerLinkActiveOptions]="{ exact: true }">
          <a routerLink="messages">Messages</a>
        </li>
        <li role="presentation" routerLinkActive="active"[routerLinkActiveOptions]="{ exact: true }">
          <a routerLink="contacts">Contacts</a>
        </li>
        <!-- <li><a (click)="onSelect('messages')">Messages</a></li>
        <li><a (click)="onSelect('contacts')">Contacts</a></li> -->
      </ul>
      <ul class="nav navbar-nav navbar-right">
        <li class="dropdown" appDropdown>
          <a href="#" class="dropdown-toggle" role="button"
            >User <span class="caret"></span
          ></a>
          <ul class="dropdown-menu">
            <li><a href="#">Login</a></li>
            <li><a href="#">Other</a></li>
            <li><a href="#">Logout</a></li>
          </ul>
        </li>
      </ul>
    </div>
  </div>
</nav>
